import { Link, useNavigate } from "react-router-dom";




const Note = ({post}) => {
const navigate = useNavigate()

    const handleDelete = (event) => {
        event.preventDefault()
        deleteNote(post.id)
        navigate('/')
    }

        return (
            <div>
            <Link to ={`/post/${post.id}`}>
                <h1>{post.title}</h1>
            </Link>
            <h2>{post.note}</h2>
            <form onSubmit={handleDelete}>
                <input type='submit' value='Delete Note'/>
            </form>

            </div>

    )
}

export default Note
