import { useState, useEffect } from 'react';
import AllNotes from './pages/AllNotes';
import SingleNote from './pages/SingleNote';
import Form from './pages/Form';
import './App.css';
import {Route, Routes} from 'react-router-dom'


const apiURL = 'https://notes-api-backend.herokuapp.com'

function App() {
  const [posts, setPosts] = useState([])

  const getNotes = async () => {
    const response = await fetch(apiURL + '/notes/')
    const data = await response.json()
    setPosts(data)
  }

  useEffect(()=> {
    getNotes()
  }, [])

  const handleFormSubmission = async (data, type) => {
    if (type === 'new'){
      const response = await fetch(`${apiURL}/notes/`, {
        method:'post',
        headers : {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(data)
      })
      getNotes()
    }else {

    }
  }




  return (
    <div className="App">
    <h1> My Note List</h1>
    <Routes>
      <Route 
      path='/'
      element={<AllNotes posts={posts} />}
      />
      <Route 
      path='/post/:id'
      element={<SingleNote posts={posts}/>}
      />
      <Route 
      path='/new'
      element={<Form handleSubmit={handleFormSubmission} buttonLabel = 'Add Note' formType=''/>}
      />
      <Route 
      path='/edit/:id'
      element={<Form posts={posts} handleSubmit={handleFormSubmission} buttonLabel= 'Edit Note' formType= ''/>}
      />
    </Routes>
    </div>
  )
}

export default App;
