import { Link, Navigate } from "react-router-dom"

  const div = {
        textAlign: "center",
        border: "5px solid",
        margin: "10px auto",
        width: "80%",
    }



    const Note = ({post, deleteNote}) => {

    const handleDelete = (event) => {
        event.preventDefault()
        Navigate()
    }

        return(
        <div style={div}>
        <Link to={`/post/${post.id}`} >
            <h1>{post.title}</h1>
        </Link>
        <h2>{post.note}</h2>
        <form onSubmit={handleDelete}>
            <input type='submit' value= 'Mark Done' />
        </form>
    </div>
    )
}

export default Note
